function orbisius_ct_highendwp_2_child_theme_child_theme_enqueue_styles() {
    $parent_style = 'HighendWP-2';
    $parent_base_dir = 'HighendWP-2';

    wp_enqueue_style( $parent_style,
        get_template_directory_uri() . '/style.css',
        array(),
        wp_get_theme( $parent_base_dir ) ? wp_get_theme( $parent_base_dir )->get('Version') : ''
    );

    wp_enqueue_style( $parent_style . '_child_style',
        get_stylesheet_directory_uri() . '/style.css',
        array( $parent_style ),
        wp_get_theme()->get('Version')
    );
}

add_action( 'wp_enqueue_scripts', 'orbisius_ct_highendwp_2_child_theme_child_theme_enqueue_styles' );
